import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Alert,
  Dimensions,
  SafeAreaView,
  ScrollView,
  Platform,
} from 'react-native';

const { width, height } = Dimensions.get('window');

const HomeScreenWeb = ({ navigation }) => {
  const [isTracking, setIsTracking] = useState(false);
  const [currentLocation, setCurrentLocation] = useState(null);
  const [routeCoordinates, setRouteCoordinates] = useState([]);
  const [startTime, setStartTime] = useState(null);
  const [distance, setDistance] = useState(0);
  const [duration, setDuration] = useState(0);
  const [watchId, setWatchId] = useState(null);

  useEffect(() => {
    return () => {
      if (watchId) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [watchId]);

  const requestLocationPermission = async () => {
    if (!navigator.geolocation) {
      Alert.alert('Error', 'Geolocation is not supported by this browser.');
      return false;
    }
    return true;
  };

  const startTracking = async () => {
    const hasPermission = await requestLocationPermission();
    if (!hasPermission) return;

    const id = navigator.geolocation.watchPosition(
      (position) => {
        const newCoordinate = {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          timestamp: new Date().toISOString(),
        };

        setCurrentLocation(newCoordinate);
        setRouteCoordinates(prev => {
          const newCoords = [...prev, newCoordinate];
          // Calculate distance
          if (newCoords.length > 1) {
            const lastCoord = prev[prev.length - 1];
            const dist = calculateDistance(lastCoord, newCoordinate);
            setDistance(prevDist => prevDist + dist);
          }
          return newCoords;
        });
      },
      (error) => {
        console.error('Location error:', error);
        Alert.alert('Location Error', 'Unable to get your location. Please ensure location services are enabled.');
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 5000
      }
    );

    setWatchId(id);
    setIsTracking(true);
    setStartTime(new Date().toISOString());
    setRouteCoordinates([]);
    setDistance(0);
    setDuration(0);

    // Start duration timer
    const timer = setInterval(() => {
      setDuration(prev => prev + 1);
    }, 1000);
  };

  const stopTracking = () => {
    if (watchId) {
      navigator.geolocation.clearWatch(watchId);
      setWatchId(null);
    }
    setIsTracking(false);
  };

  const calculateDistance = (coord1, coord2) => {
    const R = 6371e3; // Earth's radius in meters
    const φ1 = coord1.latitude * Math.PI / 180;
    const φ2 = coord2.latitude * Math.PI / 180;
    const Δφ = (coord2.latitude - coord1.latitude) * Math.PI / 180;
    const Δλ = (coord2.longitude - coord1.longitude) * Math.PI / 180;

    const a = Math.sin(Δφ/2) * Math.sin(Δφ/2) +
              Math.cos(φ1) * Math.cos(φ2) *
              Math.sin(Δλ/2) * Math.sin(Δλ/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));

    return R * c / 1000; // Convert to kilometers
  };

  const saveRoute = async () => {
    if (routeCoordinates.length === 0) {
      Alert.alert('No route to save', 'Please track a route first.');
      return;
    }

    try {
      const routeData = {
        id: Date.now().toString(),
        start_time: startTime,
        end_time: new Date().toISOString(),
        locations: routeCoordinates,
        distance: distance,
        duration: duration,
      };

      // For web, we'll use localStorage instead of AsyncStorage
      const existingRoutes = localStorage.getItem('saved_routes');
      const routes = existingRoutes ? JSON.parse(existingRoutes) : [];
      routes.push(routeData);

      localStorage.setItem('saved_routes', JSON.stringify(routes));
      Alert.alert('Success', `Route saved!\n\nDistance: ${distance.toFixed(2)} km\nTime: ${Math.floor(duration/60)}:${(duration%60).toString().padStart(2,'0')}\nPoints: ${routeCoordinates.length}`);

      // Reset tracking state
      setRouteCoordinates([]);
      setStartTime(null);
      setDistance(0);
      setDuration(0);
    } catch (error) {
      console.error('Error saving route:', error);
      Alert.alert('Error', 'Failed to save route. Please try again.');
    }
  };

  const navigateToHistory = () => {
    navigation.navigate('History');
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        {/* Header */}
        <View style={styles.header}>
          <Text style={styles.headerTitle}>🌐 Live GPS Tracker (Web)</Text>
          <Text style={styles.headerSubtitle}>Real-time location tracking in your browser</Text>
          <View style={styles.statusContainer}>
            <View style={[styles.statusDot, isTracking ? styles.activeDot : styles.inactiveDot]} />
            <Text style={styles.statusText}>{isTracking ? 'Tracking Active' : 'Ready to Track'}</Text>
          </View>
        </View>

        {/* Stats */}
        <View style={styles.statsContainer}>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{routeCoordinates.length}</Text>
            <Text style={styles.statLabel}>GPS Points</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{distance.toFixed(1)}</Text>
            <Text style={styles.statLabel}>Distance (km)</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{formatTime(duration)}</Text>
            <Text style={styles.statLabel}>Duration</Text>
          </View>
        </View>

        {/* Location Info */}
        <View style={styles.mapCard}>
          <Text style={styles.mapTitle}>Location Information</Text>
          {currentLocation ? (
            <View style={styles.locationInfo}>
              <View style={styles.locationRow}>
                <Text style={styles.locationLabel}>📍 Current Position:</Text>
              </View>
              <View style={styles.locationRow}>
                <Text style={styles.locationValue}>
                  Lat: {currentLocation.latitude.toFixed(6)}
                </Text>
              </View>
              <View style={styles.locationRow}>
                <Text style={styles.locationValue}>
                  Lng: {currentLocation.longitude.toFixed(6)}
                </Text>
              </View>
              <View style={styles.locationRow}>
                <Text style={styles.locationValue}>
                  Updated: {new Date(currentLocation.timestamp).toLocaleTimeString()}
                </Text>
              </View>
            </View>
          ) : (
            <View style={styles.noLocationContainer}>
              <Text style={styles.noLocationText}>📍 No location data yet</Text>
              <Text style={styles.noLocationSubtext}>Start tracking to see your position</Text>
            </View>
          )}

          {/* Route Visualization */}
          {routeCoordinates.length > 0 && (
            <View style={styles.routeInfo}>
              <Text style={styles.routeTitle}>🛣️ Route Progress</Text>
              <View style={styles.routeStats}>
                <Text style={styles.routeText}>Start: {new Date(startTime).toLocaleTimeString()}</Text>
                <Text style={styles.routeText}>Points collected: {routeCoordinates.length}</Text>
                <Text style={styles.routeText}>Last update: {new Date(routeCoordinates[routeCoordinates.length - 1]?.timestamp).toLocaleTimeString()}</Text>
              </View>
            </View>
          )}
        </View>

        {/* Browser Info */}
        <View style={styles.infoCard}>
          <Text style={styles.infoTitle}>ℹ️ Web Version Info</Text>
          <Text style={styles.infoText}>• Using browser's Geolocation API</Text>
          <Text style={styles.infoText}>• Data saved to localStorage</Text>
          <Text style={styles.infoText}>• Best accuracy with HTTPS</Text>
          <Text style={styles.infoText}>• Allow location permissions when prompted</Text>
        </View>

        <View style={{ height: 120 }} />
      </ScrollView>

      {/* Floating Action Buttons */}
      <View style={styles.fabContainer}>
        <TouchableOpacity
          style={[styles.fab, styles.primaryFab]}
          onPress={isTracking ? stopTracking : startTracking}
        >
          <Text style={styles.fabText}>{isTracking ? '⏹️ Stop' : '▶️ Start'}</Text>
        </TouchableOpacity>

        {!isTracking && routeCoordinates.length > 0 && (
          <TouchableOpacity
            style={[styles.fab, styles.saveFab]}
            onPress={saveRoute}
          >
            <Text style={styles.fabText}>💾 Save</Text>
          </TouchableOpacity>
        )}

        <TouchableOpacity
          style={[styles.fab, styles.historyFab]}
          onPress={navigateToHistory}
        >
          <Text style={styles.fabText}>📊 History</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

