import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Alert,
  Dimensions,
  SafeAreaView,
  ScrollView,
} from 'react-native';
import MapView, { Marker, Polyline } from 'react-native-maps';
import * as Location from 'expo-location';
import AsyncStorage from '@react-native-async-storage/async-storage';

const { width, height } = Dimensions.get('window');

const HomeScreenSimple = ({ navigation }) => {
  const [isTracking, setIsTracking] = useState(false);
  const [currentLocation, setCurrentLocation] = useState(null);
  const [routeCoordinates, setRouteCoordinates] = useState([]);
  const [startTime, setStartTime] = useState(null);
  const [locationSubscription, setLocationSubscription] = useState(null);
  const [distance, setDistance] = useState(0);
  const [duration, setDuration] = useState(0);
  const mapRef = useRef(null);

  useEffect(() => {
    requestLocationPermission();
    return () => {
      if (locationSubscription) {
        locationSubscription.remove();
      }
    };
  }, []);

  const requestLocationPermission = async () => {
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert(
          'Permission denied',
          'Location permission is required to track your route.'
        );
        return;
      }
    } catch (error) {
      console.error('Error requesting location permission:', error);
    }
  };

  const startTracking = async () => {
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permission denied', 'Location permission is required.');
        return;
      }

      const subscription = await Location.watchPositionAsync(
        {
          accuracy: Location.Accuracy.High,
          timeInterval: 5000, // 5 seconds
          distanceInterval: 10, // 10 meters
        },
        (location) => {
          const newCoordinate = {
            latitude: location.coords.latitude,
            longitude: location.coords.longitude,
            timestamp: new Date().toISOString(),
          };

          setCurrentLocation(newCoordinate);
          setRouteCoordinates(prev => {
            const newCoords = [...prev, newCoordinate];
            // Calculate distance
            if (newCoords.length > 1) {
              const lastCoord = prev[prev.length - 1];
              const dist = calculateDistance(lastCoord, newCoordinate);
              setDistance(prevDist => prevDist + dist);
            }
            return newCoords;
          });

          // Center map on current location
          if (mapRef.current) {
            mapRef.current.animateToRegion({
              latitude: newCoordinate.latitude,
              longitude: newCoordinate.longitude,
              latitudeDelta: 0.01,
              longitudeDelta: 0.01,
            });
          }
        }
      );

      setLocationSubscription(subscription);
      setIsTracking(true);
      setStartTime(new Date().toISOString());
      setRouteCoordinates([]);
      setDistance(0);
      setDuration(0);

      // Start duration timer
      const timer = setInterval(() => {
        if (isTracking) {
          setDuration(prev => prev + 1);
        }
      }, 1000);

    } catch (error) {
      console.error('Error starting tracking:', error);
      Alert.alert('Error', 'Failed to start tracking. Please try again.');
    }
  };

  const stopTracking = () => {
    if (locationSubscription) {
      locationSubscription.remove();
      setLocationSubscription(null);
    }
    setIsTracking(false);
  };

  const calculateDistance = (coord1, coord2) => {
    const R = 6371e3; // Earth's radius in meters
    const œÜ1 = coord1.latitude * Math.PI / 180;
    const œÜ2 = coord2.latitude * Math.PI / 180;
    const ŒîœÜ = (coord2.latitude - coord1.latitude) * Math.PI / 180;
    const ŒîŒª = (coord2.longitude - coord1.longitude) * Math.PI / 180;

    const a = Math.sin(ŒîœÜ/2) * Math.sin(ŒîœÜ/2) +
              Math.cos(œÜ1) * Math.cos(œÜ2) *
              Math.sin(ŒîŒª/2) * Math.sin(ŒîŒª/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));

    return R * c / 1000; // Convert to kilometers
  };

  const saveRoute = async () => {
    if (routeCoordinates.length === 0) {
      Alert.alert('No route to save', 'Please track a route first.');
      return;
    }

    try {
      const routeData = {
        id: Date.now().toString(),
        start_time: startTime,
        end_time: new Date().toISOString(),
        locations: routeCoordinates,
        distance: distance,
        duration: duration,
      };

      const existingRoutes = await AsyncStorage.getItem('saved_routes');
      const routes = existingRoutes ? JSON.parse(existingRoutes) : [];
      routes.push(routeData);

      await AsyncStorage.setItem('saved_routes', JSON.stringify(routes));
      Alert.alert('Success', `Route saved!\n\nDistance: ${distance.toFixed(2)} km\nTime: ${Math.floor(duration/60)}:${(duration%60).toString().padStart(2,'0')}\nPoints: ${routeCoordinates.length}`);

      // Reset tracking state
      setRouteCoordinates([]);
      setStartTime(null);
      setDistance(0);
      setDuration(0);
    } catch (error) {
      console.error('Error saving route:', error);
      Alert.alert('Error', 'Failed to save route. Please try again.');
    }
  };

  const navigateToHistory = () => {
    navigation.navigate('History');
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        {/* Header */}
        <View style={styles.header}>
          <Text style={styles.headerTitle}>üèÉ‚Äç‚ôÇÔ∏è Live GPS Tracker</Text>
          <Text style={styles.headerSubtitle}>Real-time location tracking</Text>
          <View style={styles.statusContainer}>
            <View style={[styles.statusDot, isTracking ? styles.activeDot : styles.inactiveDot]} />
            <Text style={styles.statusText}>{isTracking ? 'Tracking Active' : 'Ready to Track'}</Text>
          </View>
        </View>

        {/* Stats */}
        <View style={styles.statsContainer}>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{routeCoordinates.length}</Text>
            <Text style={styles.statLabel}>GPS Points</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{distance.toFixed(1)}</Text>
            <Text style={styles.statLabel}>Distance (km)</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{formatTime(duration)}</Text>
            <Text style={styles.statLabel}>Duration</Text>
          </View>
        </View>

        {/* Map */}
        <View style={styles.mapCard}>
          <Text style={styles.mapTitle}>Live Map</Text>
          <View style={styles.mapContainer}>
            <MapView
              ref={mapRef}
              style={styles.map}
              initialRegion={{
                latitude: currentLocation?.latitude || 37.78825,
                longitude: currentLocation?.longitude || -122.4324,
                latitudeDelta: 0.01,
                longitudeDelta: 0.01,
              }}
              showsUserLocation={true}
              followsUserLocation={isTracking}
            >
              {currentLocation && (
                <Marker
                  coordinate={currentLocation}
                  title="Your Location"
                  pinColor="blue"
                />
              )}

              {routeCoordinates.length > 0 && (
                <Polyline
                  coordinates={routeCoordinates}
                  strokeColor="#FF6B35"
                  strokeWidth={4}
                />
              )}

              {routeCoordinates.length > 0 && (
                <Marker
                  coordinate={routeCoordinates[0]}
                  title="Start"
                  pinColor="green"
                />
              )}

              {routeCoordinates.length > 0 && (
                <Marker
                  coordinate={routeCoordinates[routeCoordinates.length - 1]}
                  title="Current"
                  pinColor="red"
                />
              )}
            </MapView>
          </View>
        </View>

        <View style={{ height: 120 }} />
      </ScrollView>

      {/* Floating Action Buttons */}
      <View style={styles.fabContainer}>
        <TouchableOpacity
          style={[styles.fab, styles.primaryFab]}
          onPress={isTracking ? stopTracking : startTracking}
        >
          <Text style={styles.fabText}>{isTracking ? '‚èπÔ∏è Stop' : '‚ñ∂Ô∏è Start'}</Text>
        </TouchableOpacity>

        {!isTracking && routeCoordinates.length > 0 && (
          <TouchableOpacity
            style={[styles.fab, styles.saveFab]}
            onPress={saveRoute}
          >
            <Text style={styles.fabText}>üíæ Save</Text>
          </TouchableOpacity>
        )}

        <TouchableOpacity
          style={[styles.fab, styles.historyFab]}
          onPress={navigateToHistory}
        >
          <Text style={styles.fabText}>üìä History</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  scrollView: {
    flex: 1,
  },
  header: {
    backgroundColor: '#FF6B35',
    padding: 20,
    paddingTop: 10,
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
  },
  headerSubtitle: {
    fontSize: 16,
    color: '#fff',
    textAlign: 'center',
    opacity: 0.9,
    marginTop: 5,
  },
  statusContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 15,
